Rendu = function(game) {
      // Appel des variables nécéssaires
      this.game = game;
      var scene = game.scene;

      // Création de notre lumière principale
      var light = new BABYLON.HemisphericLight("light1", new BABYLON.Vector3(0, 10, 0), scene);
      var light2 = new BABYLON.HemisphericLight("light2", new BABYLON.Vector3(0, -1, 0), scene);
      light2.intensity = 0.8;
      //light.diffuse =  BABYLON.Color3.Purple();

    var materialGround = new BABYLON.StandardMaterial("ground", scene);
    materialGround.diffuseTexture = new BABYLON.Texture("data:image/jpeg;base64,", scene);
    materialGround.diffuseTexture.uScale = 500.0;
    materialGround.diffuseTexture.vScale = 500.0;

    // Material pour les objets
      var materialWall = new BABYLON.StandardMaterial("wall", scene);
      materialWall.diffuseTexture = new BABYLON.Texture("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRvue3Wc2HS-plhIAMKUvNZ553TlWjfpN4TmS1HICrCbj9hemV3&s", scene);
      materialWall.diffuseTexture.uScale = 4.0;
      materialWall.diffuseTexture.vScale = 4.0;

      var ground = BABYLON.Mesh.CreateGround("ground1", 1000, 1000, 1, scene);
      ground.material = materialGround;
      ground.position.y = -13;
      ground.scaling.y = 3;
      ground.scaling.x = 3.5;
      ground.checkCollisions = true;

      var background = new BABYLON.Layer("back", "data:image/jpeg;base64,", scene);
      background.isBackground = true;
      materialGround.diffuseTexture.uScale = 1000.0;
      materialGround.diffuseTexture.vScale = 750.0;


      //Create dynamic texture
      var textureResolution = 512;
      var textureStade = new BABYLON.DynamicTexture("dynamic texture", {width:500, height:500}, scene);
      var textureContext = textureStade.getContext();

      var materialStade = new BABYLON.StandardMaterial("Mat", scene);
      materialStade.diffuseTexture = textureStade;


      //Add text to dynamic texture
      var font = "bold 44px monospace";
      textureStade.drawText("Ǝᗡ∀⊥S", 250, 350, font, "green", "white", true, true);

      var sphere = BABYLON.Mesh.CreateSphere("foot", 100, 100, scene);
      sphere.position= new BABYLON.Vector3(-400, -15, 400);
      sphere.material = materialStade;
      sphere.rotate.x = (90*Math.PI)/180;


      var columns = [];
      var numberColumn = 81;
      var sizeArena = 1000*ground.scaling.x -500;
      var ratio = ((115/numberColumn)/100) * sizeArena;
      for (var i = 0; i <= 15; i++) {
          var size = getRandomIntInclusive(40,100);
          if(numberColumn>0){
              columns[i] = [];
              let mainCylinder = BABYLON.Mesh.CreateCylinder("cyl0-"+i, size , 30, 30, 4, 4, scene);
              mainCylinder.position = new BABYLON.Vector3(1725, size/2-15 ,-480 + (50 * i));
              mainCylinder.rotation.y = (Math.PI*45)/180;
              mainCylinder.material = materialWall;
              mainCylinder.checkCollisions = true;

              columns[i].push(mainCylinder);

              if(numberColumn>4){
                  for (let y = 1; y <= numberColumn - 1; y++) {
                      size = getRandomIntInclusive(40,100);
                      let newCylinder = BABYLON.Mesh.CreateCylinder("cyl0-"+i, size , 30, 30, 4, 4, scene);
                      newCylinder.position = new BABYLON.Vector3(1725 - (ratio*y), size/2 -15, columns[i][0].position.z);
                      newCylinder.rotation.y = (Math.PI*45)/180;;
                      newCylinder.material = materialWall;
                      newCylinder.checkCollisions = true;
                      columns[i].push(newCylinder);
                  }
              }
          }
      }
  };

  function getRandomIntInclusive(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min +1)) + min;
  }
